import axios from 'axios'
import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import data from './data'
import { useReducer } from 'react'

export default function useFunctions() {

    const [APIUSERS, setAPIU] = useState([])
    const [APIA, setAPIA] = useState([])
    const [Reducer, setReducer] = useReducer(x => x + 1, 0)
    useEffect(() => {
        axios.get(data.Mainurl + data.GET).then((res) => {
            setAPIU(res.data)
        })

        axios.get(data.Mainurl + data.GETA).then((res) => {
            setAPIA(res.data)
        })
    }, [Reducer])

    const [Data, setData] = useState('')
    let array = []
    for (let x = 0; x < Data.length; x++) {
        array.push(Data[x]?.value)
    }

    const [Preview, setPreview] = useState(undefined)
    const [lat, setlat] = useState('')
    const [lng, setlng] = useState('')
    const [text, settext] = useState('')

    let lan = []
    lat.push(lat)
    lat.push(lng)

    function Submit() {
        if(APIA.length > 0){
            
        }
        axios.post(data.Mainurl + data.POST, {
            "users": array,
            "ab_image": Preview.name,
            "ab_text": text,
            "map": lan,
        }).then((res) => {
            setReducer()
        })
    }

    return {
        APIUSERS,
        Submit,
        setData,

        setPreview,
        Preview,
        setlat,
        setlng,
        settext,
    }
}
